import React, { useState, useEffect } from 'react';
import './App.css';

function SensorDemo() {
    const [status, setStatus] = useState('');
    const [imageSrc, setImageSrc] = useState(null);

    const fetchImage = async () => {
        try {
            const response = await fetch('http://127.0.0.1:5000/image');
            if (response.ok) {
                const blob = await response.blob();
                setImageSrc(URL.createObjectURL(blob)); // Convert blob to URL
            } else {
                console.error('Failed to fetch image');
            }
        } catch (error) {
            console.error('Error fetching image:', error);
        }
    };

    useEffect(() => {
        fetchImage();
    }, []); // Fetch image on component mount

    const handleImageUpload = async (event) => {
        const file = event.target.files[0];
        const formData = new FormData();
        formData.append('file', file);

        try {
            const response = await fetch('http://127.0.0.1:5000/upload', {
                method: 'POST',
                body: formData
            });

            if (response.ok) {
                const data = await response.json();
                setStatus('File uploaded successfully: ' + data.filename);
                fetchImage(); // Fetch the updated image after upload
            } else {
                setStatus('Failed to upload file');
            }
        } catch (error) {
            console.error('Error uploading file:', error);
            setStatus('Failed to upload file');
        }
    };

    const handleButtonClick = (endpoint) => {
        fetch('http://127.0.0.1:5000' + endpoint, { method: 'POST' })
            .then(response => response.json())
            .then(data => setStatus('Variable toggled: ' + data.toggle_variable))
            .catch(error => console.error(error));
    };

    return (
        <div>
            <h1>
                <span>&nbsp;Image processing</span>
            </h1>

            <div>
                <div className="title">
                    <h2>Image Preview</h2>
                </div>
                <div className="box">
                    <div style={{ width: '40%', height: '500px' }}>
                        {imageSrc && (
                            <img src={imageSrc} alt="Uploaded Image" style={{ width: '100%', height: '100%' }} />
                        )}
                    </div>
                    <div style={{ width: '60%', height: '500px' }}>
                        <div className="grid-container">
                            <input type="file" accept="image/*" onChange={handleImageUpload} />
                            <button onClick={() => handleButtonClick('/model1')} id="model1">Yolo Model 1</button>
                            <button onClick={() => handleButtonClick('/model2')} id="model2">Yolo Model 2</button>
                            <button onClick={() => handleButtonClick('/bw')} id="bw">Black and white</button>
                            <button onClick={() => handleButtonClick('/sharp')} id="sharp">Sharpen</button>
                            <button onClick={() => handleButtonClick('/pause')} id="pause">Pause</button>
                            <button onClick={() => handleButtonClick('/fr')} id="fr">Change frame rate</button>
                        </div>
                    </div>
                </div>
            </div>

            <div id="status">{status}</div>
        </div>
    );
}

export default SensorDemo;
