import React, { useState, useEffect } from 'react';
import './App.css';

function SensorDemo() {
    const [status, setStatus] = useState('');
    const [imageSrc, setImageSrc] = useState(null);

    const fetchImage = async () => {
        try {
            const response = await fetch('http://127.0.0.1:5000/image');
            if (response.ok) {
                setImageSrc(await response.blob());
            } else {
                console.error('Failed to fetch image');
            }
        } catch (error) {
            console.error('Error fetching image:', error);
        }
    };

    useEffect(() => {
        fetchImage();
    }, []); // Fetch image on component mount

    const handleImageUpload = async (event) => {
        // Your existing image upload logic
    };

    const handleButtonClick = (endpoint) => {
        // Your existing button click logic
    };

    return (
        <div>
            <h1>
                <span>&nbsp;Image processing</span>
            </h1>

            <div>
                <div className="title">
                    <h2>Image Preview</h2>
                </div>
                <div className="box">
                    <div style={{ width: '40%', height: '500px' }}>
                        {imageSrc && (
                            <img src={URL.createObjectURL(imageSrc)} alt="Uploaded Image" width="100%" />
                        )}
                    </div>
                    <div style={{ width: '60%', height: '500px' }}>
                        <div className="grid-container">
                            {/* Your existing buttons */}
                        </div>
                    </div>
                </div>
            </div>

            <div id="status">{status}</div>
        </div>
    );
}

export default SensorDemo;
